import { motion } from "framer-motion";

export const About = () => {
    return (
        <section id="about" className="h-screen flex flex-col items-center justify-center bg-blue-100 px-6 text-center">
        <motion.h2 
          className="text-3xl font-bold text-blue-600"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          viewport={{ once: true }}
        >
          About Us
        </motion.h2>

        <motion.p 
          className="mt-4 max-w-3xl text-gray-700 leading-relaxed"
          initial={{ opacity: 0, x: -50 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.2, ease: "easeOut" }}
          viewport={{ once: true }}
        >
          Welcome to <strong>Digital Bootup</strong>, your one-stop solution for cutting-edge digital services. 
          We specialize in digital marketing, video editing, graphic designing, and web and application development, 
          helping businesses thrive in the digital world.
        </motion.p>

        <motion.p 
          className="mt-4 max-w-3xl text-gray-700 leading-relaxed"
          initial={{ opacity: 0, x: 50 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.4, ease: "easeOut" }}
          viewport={{ once: true }}
        >
          Our expert team crafts tailored strategies to boost your online presence, engage your audience, and drive growth. 
          Whether you need stunning visuals, seamless websites, or high-impact marketing campaigns, we’ve got you covered.
        </motion.p>

        <motion.p 
          className="mt-4 max-w-3xl text-gray-700 leading-relaxed"
          initial={{ opacity: 0, scale: 0.8 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8, delay: 0.6, ease: "easeOut" }}
          viewport={{ once: true }}
        >
          Let’s elevate your brand with creativity, innovation, and results-driven solutions. 
          Come meet our dedicated consultants, ready to assist you every step of the way.
        </motion.p>
      </section>
    )
}
